mapper.select:
select * from ct_merchant where status != 2
and id in

    #{item}

select name merchantName,headUrl merchantIcon
from  ct_merchant merchant
where id = #{marId}

select m.* ,ROUND((ROUND(6378.138*2*ASIN(SQRT(POW(SIN((#{latitude}*PI()/180-lat*PI()/180)/2),2)+
 COS(#{latitude}*PI()/180)*COS(lat*PI()/180)*POW(SIN((#{longitude}*PI()/180-log*PI()/180)/2),2)))*1000))/1000,2) AS distance
 from ct_merchant m left join  ct_good g  on ( find_in_set(m.id,g.mart_id) )
   where 1 = 1 and g.status = 1

     and  g.name  like  CONCAT('%',#{value},'%')

group by m.id
having m.status=1

     and distance <= #{distanced}


     and m.recommend = #{recommend}

select * ,ROUND((ROUND(6378.138*2*ASIN(SQRT(POW(SIN((#{latitude}*PI()/180-lat*PI()/180)/2),2)+ COS(#{latitude}*PI()/180)*COS(lat*PI()/180)*POW(SIN((#{longitude}*PI()/180-log*PI()/180)/2),2)))*1000))/1000,2) AS distance from ct_merchant having status=1 and categoryId like CONCAT('%',#{acategoryId},'%')
and distance <= #{distanced}
and recommend = #{recommend}
and categoryId like CONCAT('%',#{categoryId},'%')

select * ,ROUND((ROUND(6378.138*2*ASIN(SQRT(POW(SIN((#{latitude}*PI()/180-lat*PI()/180)/2),2)+ COS(#{latitude}*PI()/180)*COS(lat*PI()/180)*POW(SIN((#{longitude}*PI()/180-log*PI()/180)/2),2)))*1000))/1000,2) AS distance from ct_merchant having status=1
and name like CONCAT('%',#{value},'%')
and distance <= #{distanced}
and recommend = #{recommend}

mapper.update:
update ct_merchant set days=#{status}

    id in

        ${ids}
