mapper.update:
update mulit_coupon_boundle set
consume_value = #{consumeValue}
consume_value = coupon_value, use_status = 1
where id in
#{ids,jdbcType = BIGINT}

update mulit_coupon_boundle set
consume_value = #{consumeValue}
consume_value = coupon_value, use_status = 0
where id in
#{ids,jdbcType = BIGINT}

mapper.select:
SELECT mu.*, ct.mobile, ( CASE WHEN mu.expire_pmt > NOW() THEN 0 ELSE 1 END
) as flag FROM `mulit_coupon_boundle` mu LEFT JOIN ct_client_user ct ON mu.owner_id = ct.id WHERE mu.deleted = 0
and mu.expire_pmt > now()
and mu.expire_pmt <= now()
and mu.use_status = #{useStatus}
and date_format(mu.got_pmt, '%Y-%m-%d') >= #{startTime}
and date_format(mu.got_pmt, '%Y-%m-%d') <= #{endTime}
and ct.mobile = #{mobile}
ORDER BY mu.got_pmt DESC
