select t.name,SUM(t.count) as count,SUM(t.total) as total ,t.icon as url from ( SELECT cg.`name`, cg.icon, ctso.quantity-ctso.refund_quantity as count, ctso.price*(ctso.quantity-ctso.refund_quantity) as total from ct_slave_order ctso LEFT JOIN ct_good cg on ctso.good_id = cg.id WHERE ctso.order_id in ( select ctmo.order_id from ct_master_order ctmo where ctmo.merchant_id = #{merchantId} and ctmo.`status` = 10
and date_format(ctmo.create_date, '%Y-%m-%d') >= #{startDate}
and date_format(ctmo.create_date, '%Y-%m-%d') <= #{endDate}
) and ctso.`status` = 10 ) t GROUP BY name ORDER BY
total
count
asc
desc

select
ccu.username as userName,
COUNT(ccu.username) as count,
(
SELECT IFNULL(SUM(ctmo1.merchant_proceeds),0) FROM ct_master_order ctmo1 where ctmo1.creator = ccu.id and ctmo1.`status` = 10 and ctmo1.merchant_id = #{merchantId}

    and date_format(ctmo1.create_date, '%Y-%m-%d') >= #{startDate}


    and date_format(ctmo1.create_date, '%Y-%m-%d') <= #{endDate}

) as completeTotal,
(
SELECT IFNULL(count(ctmo1.order_id),0) FROM ct_master_order ctmo1 where ctmo1.creator = ccu.id and ctmo1.`status` = 10 and ctmo1.merchant_id = #{merchantId}

    and date_format(ctmo1.create_date, '%Y-%m-%d') >= #{startDate}


    and date_format(ctmo1.create_date, '%Y-%m-%d') <= #{endDate}

and ctmo1.p_order_id = '0'
) as completeCount,
(
SELECT IFNULL(SUM(ctmo3.merchant_proceeds),0) FROM ct_master_order ctmo3 where ctmo3.creator = ccu.id and ctmo3.`status` in (3,5,8) and ctmo3.merchant_id = #{merchantId}

    and date_format(ctmo3.create_date, '%Y-%m-%d') >= #{startDate}


    and date_format(ctmo3.create_date, '%Y-%m-%d') <= #{endDate}

and ctmo3.p_order_id = '0'
) as cancelTotal,
(
SELECT IFNULL(count(ctmo3.order_id),0) FROM ct_master_order ctmo3 where ctmo3.creator = ccu.id and ctmo3.`status` in (3,5,8) and ctmo3.merchant_id = #{merchantId}

    and date_format(ctmo3.create_date, '%Y-%m-%d') >= #{startDate}


    and date_format(ctmo3.create_date, '%Y-%m-%d') <= #{endDate}

and ctmo3.p_order_id = '0'
) as cancelCount
from
ct_master_order ctmo
LEFT JOIN
ct_client_user ccu ON ctmo.creator = ccu.id
WHERE
ctmo.merchant_id = #{merchantId} and ctmo.`status` in (10 ,8 , 5, 3)

    and date_format(ctmo.create_date, '%Y-%m-%d') >= #{startDate}


    and date_format(ctmo.create_date, '%Y-%m-%d') <= #{endDate}

and ctmo.p_order_id = '0'
GROUP BY
ccu.username
ORDER BY

    count


    completeTotal


    completeCount


    cancelTotal


    cancelCount


    asc


    desc

SELECT
SUM(ctmo.merchant_proceeds)
FROM
ct_master_order ctmo
where
ctmo.merchant_id = #{merchantId}
and ctmo.`status` = 10

    and date_format(ctmo.create_date, '%Y-%m-%d') >= #{startTime1}


    and date_format(ctmo.create_date, '%Y-%m-%d') <= #{endTime1}

select ctm.id, ctm.`name`, ctm.mobile, ctm.total_cash as totalCash, ctm.already_cash as alreadyCash, ctm.not_cash as notCash, ctm.wart_cash as wartCash, ctm.point_money as pointMoney, IFNULL(( SELECT sum(ctmw.money) FROM ct_merchant_withdraw ctmw WHERE ctmw.merchant_id = ctm.id AND ctmw.type = 1 AND ctmw.`status`=1 and ctmw.verify_state = 2 ),0) AS wxTotal, IFNULL(( SELECT sum(ctmw.money) FROM ct_merchant_withdraw ctmw WHERE ctmw.merchant_id = ctm.id AND ctmw.type = 2 AND ctmw.`status`=1 and ctmw.verify_state = 2 ),0) AS aliTotald, ( SELECT COUNT(ctmo.id) from ct_master_order ctmo where ctm.id = ctmo.merchant_id and ctmo.`status` = 10 and date_format(ctmo.eat_time, '%Y-%m') in
#{item}
) as count from ct_merchant ctm
and ctm.`name` like concat('%', #{name}, '%')
and ctm.mobile like concat('%', #{mobile}, '%')
and ctm.id = #{id}
GROUP BY ctm.total_cash desc

select sum(ctm.total_cash) as totalCash, sum(ctm.already_cash) as alreadyCash, sum(ctm.not_cash) as notCash, sum(ctm.wart_cash) as wartCash, sum(ctm.point_money) as pointMoney, sum(IFNULL(( SELECT sum(ctmw.money) FROM ct_merchant_withdraw ctmw WHERE ctmw.merchant_id = ctm.id AND ctmw.type = 1 AND ctmw.`status`=1 and ctmw.verify_state = 2 ),0)) AS wxTotal, sum(IFNULL(( SELECT sum(ctmw.money) FROM ct_merchant_withdraw ctmw WHERE ctmw.merchant_id = ctm.id AND ctmw.type = 2 AND ctmw.`status`=1 and ctmw.verify_state = 2 ),0)) AS aliTotald, sum(( SELECT COUNT(ctmo.id) from ct_master_order ctmo where ctm.id = ctmo.merchant_id and ctmo.`status` = 10 and date_format(ctmo.eat_time, '%Y-%m') in
#{item}
)) as count from ct_merchant ctm
and ctm.`name` like concat('%', #{name}, '%')
and ctm.mobile like concat('%', #{mobile}, '%')
and ctm.id = #{id}

SELECT count(0) FROM `ct_master_order`
where merchant_id = #{merchantId}
and `status` = 10
and p_order_id = '0'

    and date_format(create_date, '%Y-%m-%d') >= #{startTime1}


    and date_format(create_date, '%Y-%m-%d') <= #{endTime1}

select ctmo.order_id as orderId, ctmo.contacts, ctmo.contact_number as contactNumber, ( select ctmr.`name` from ct_merchant_room ctmr where ctmr.id = ctmo.room_id ) as roomName, ctmo.pay_money as payMoney, ctmo.description, ctmo.eat_time as eatTime from ct_master_order ctmo LEFT JOIN ct_slave_order ctso on ctso.order_id = ctmo.order_id and ctmo.merchant_id = #{merchantId} where ctmo.`status` = 2 and ctso.`status` = 6 and ctmo.merchant_id = #{merchantId}
and ctmo.order_id like concat('%', #{orderId}, '%')
ORDER BY ctmo.eat_time ASC

SELECT ctdt.pay_date as payDate,ctdt.merchant_id as merchantId,ctdt.pay_type as payType,sum(ctdt.order_total) as orderTotal, sum(ctdt.service_chanrge) as serviceChanrge,sum(ctdt.merchant_proceeds) as merchantProceeds,sum(ctdt.platform_brokerage) as platformBrokerage, sum(ctdt.reality_money) as realityMoney,sum(ctdt.merchant_discount_amount) as merchantDiscount_amount,sum(ctdt.gift_money) as giftMoney , ctm.`name`, ctm.id as mid, sum(ctdt.platform_brokerage-ctdt.gift_money) as platformRealityMoney from ct_days_together ctdt left join ct_merchant ctm on ctm.id = ctdt.merchant_id
and date_format(ctdt.pay_date, '%Y-%m-%d') >= #{startDate}
and date_format(ctdt.pay_date, '%Y-%m-%d') <= #{endDate}
and ctdt.pay_type = #{payType}
group by ctdt.id

SELECT sum(ctdt.order_total) as orderTotal, sum(ctdt.service_chanrge) as serviceChanrge, sum(ctdt.merchant_proceeds) as merchantProceeds, sum(ctdt.platform_brokerage) as platformBrokerage, sum(ctdt.reality_money) as realityMoney, sum(ctdt.merchant_discount_amount) as merchantDiscount_amount, sum(ctdt.gift_money) as giftMoney, sum(ctdt.platform_brokerage-ctdt.gift_money) as platformRealityMoney from ct_days_together ctdt left join ct_merchant ctm on ctm.id = ctdt.merchant_id
and date_format(ctdt.pay_date, '%Y-%m-%d') >= #{startDate}
and date_format(ctdt.pay_date, '%Y-%m-%d') <= #{endDate}
and ctdt.pay_type = #{payType}

SELECT sum(case pay_type when 1 then 1 else 0 end) as banalceCount, sum(case pay_type when 2 then 1 else 0 end) as alipayCount, sum(case pay_type when 3 then 1 else 0 end) as wxpayCount, sum(case pay_type when 4 then 1 else 0 end) as bankcardCount, sum(order_total) AS orderTotal, sum(service_chanrge) AS serviceChanrge, sum(merchant_proceeds) AS merchantProceeds, sum(platform_brokerage) AS platformBrokerage, sum(reality_money) AS realityMoney, sum(merchant_discount_amount) AS merchantDiscountAmount, sum(gift_money) AS giftMoney FROM ct_days_together
and pay_date >= #{startDate}
and pay_date <= #{endDate}
and pay_type = #{payType}

SELECT sdd.id, sdd.create_date as createDate, sdd.order_id as orderId, sdd.pay_type as payType, sdd.incident_type as incidentType, sdd.pay_mobile as payMobile, sdd.pay_merchant_name as payMerchantName, sdd.pay_merchant_id as payMerchantId, sdd.order_total as orderTotal, sdd.merchant_discount_amount as merchantDiscountAmount, sdd.transaction_amount as transactionAmount, sdd.gift_money as giftMoney, sdd.reality_money as realityMoney, sdd.platform_brokerage as platformBrokerage, sdd.merchant_proceeds as merchantProceeds, sdd.withdraw_money as withdrawMoney, sdd.platform_balance as platformBalance, sdd.wx_paymoney as wxPaymoney, sdd.ali_paymoney as aliPaymoney, sdd.service_charge as serviceCharge, sdd.update_date as updateDate, case when sdd.incident_type = 0 then (select ro.order_id from refund_order ro where ro.refund_id = sdd.order_id) else '' end as tgoodOrderId, case when sdd.incident_type = 8 then (select ctmo.order_id from ct_master_order ctmo where ctmo.refund_id = sdd.order_id) else '' end as torderId FROM stats_day_detail sdd
and date_format(sdd.create_date, '%Y-%m-%d') >= #{startDate}
and date_format(sdd.create_date, '%Y-%m-%d') <= #{endDate}

SELECT IFNULL(SUM(CASE when sdd.order_total>0 then sdd.order_total else 0 end),0) as orderTotal, IFNULL(SUM(CASE when sdd.merchant_discount_amount>0 then sdd.merchant_discount_amount else 0 end),0) as merchantDiscountAmount, IFNULL(SUM(CASE when sdd.transaction_amount>0 then sdd.transaction_amount else 0 end),0) as transactionAmount, IFNULL(SUM(CASE when sdd.gift_money>0 then sdd.gift_money else 0 end),0) as giftMoney, IFNULL(SUM(CASE when sdd.reality_money>0 then sdd.reality_money else 0 end),0) as realityMoney, IFNULL(SUM(CASE when sdd.platform_brokerage>0 then sdd.platform_brokerage else 0 end),0) as platformBrokerage, IFNULL(SUM(CASE when sdd.merchant_proceeds>0 then sdd.merchant_proceeds else 0 end),0) as merchantProceeds, IFNULL(SUM(CASE when sdd.withdraw_money>0 then sdd.withdraw_money else 0 end),0) as withdrawMoney, IFNULL(SUM(CASE when sdd.platform_balance>0 then sdd.platform_balance else 0 end),0) as platformBalance, IFNULL(SUM(CASE when sdd.wx_paymoney>0 then sdd.wx_paymoney else 0 end),0) as wxPaymoney, IFNULL(SUM(CASE when sdd.ali_paymoney>0 then sdd.ali_paymoney else 0 end),0) as aliPaymoney, IFNULL(SUM(CASE when sdd.service_charge>0 then sdd.service_charge else 0 end),0) as serviceCharge FROM stats_day_detail sdd
and date_format(sdd.create_date, '%Y-%m-%d') >= #{startDate}
and date_format(sdd.create_date, '%Y-%m-%d') <= #{endDate}

SELECT

IFNULL(SUM( case when `status` = 10 then merchant_proceeds else 0 end ),0) as merchantTotalCash,

    IFNULL(SUM( case when `status` = 10 and pay_mode = 1 then pay_money else 0 end ),0) as balanceTotalCash,
    IFNULL(SUM( case when `status` = 10 and pay_mode = 2 then pay_money else 0 end ),0) as aliTotalCash,
    IFNULL(SUM( case when `status` = 10 and pay_mode = 3 then pay_money else 0 end ),0) as wxTotalCash,


    IFNULL(SUM( case when `status` = 10 and pay_mode = 1 then merchant_proceeds else 0 end ),0) as balanceTotalCash,
    IFNULL(SUM( case when `status` = 10 and pay_mode = 2 then merchant_proceeds else 0 end ),0) as aliTotalCash,
    IFNULL(SUM( case when `status` = 10 and pay_mode = 3 then merchant_proceeds else 0 end ),0) as wxTotalCash,


    IFNULL(SUM( case when `status` = 10 then platform_brokerage else 0 end ),0) as pointMoney,
    IFNULL(SUM( case when `status` = 10 then merchant_proceeds+platform_brokerage else 0 end ),0) as totalCash,
    ( select IFNULL(COUNT(0),0) from ct_merchant


         and date_format(create_date, '%Y-%m-%d') >= #{startDate} 


         and date_format(create_date, '%Y-%m-%d') <= #{endDate} 


    ) as merchantCount,
    (select IFNULL(COUNT(0),0) from ct_client_user ccu
    where ccu.status != 9

         and date_format(ccu.create_date, '%Y-%m-%d') >= #{startDate} 


         and date_format(ccu.create_date, '%Y-%m-%d') <= #{endDate} 

    ) as userCount,

IFNULL(SUM( case when `status` = 10 and p_order_id = '0' then 1 else 0 end ),0) as pOrderCount,
IFNULL(SUM( case when `status` = 10 and p_order_id != '0' then 1 else 0 end ),0) as orderCount
FROM
    ct_master_order


         and date_format(eat_time, '%Y-%m-%d') >= #{startDate} 


         and date_format(eat_time, '%Y-%m-%d') <= #{endDate} 


        and merchant_id = #{merchantId}

select * from ct_merchant


        and name like CONCAT('%',#{name},'%')


        and mobile like CONCAT('%',#{mobile},'%')


         and date_format(create_date, '%Y-%m-%d') >= #{startDate} 


         and date_format(create_date, '%Y-%m-%d') <= #{endDate} 


        and auditStatus = #{auditStatus}



       and id = #{id}


ORDER BY create_date desc

select date_format(create_date, '%Y-%m-%d') as dataRow, count(id) as dataCount from ct_client_user
and date_format(create_date, '%Y-%m-%d') >= #{startDate}
and date_format(create_date, '%Y-%m-%d') <= #{endDate}
GROUP BY date_format(create_date, '%Y-%m-%d')

select date_format(create_date, '%Y-%m') as dataRow, count(id) as dataCount from ct_client_user
and date_format(create_date, '%Y') = #{year}
GROUP BY date_format(create_date, '%Y-%m')

select date_format(create_date, '%Y-%m-%d') as dataRow, count(id) as dataCount from ct_merchant
and date_format(create_date, '%Y-%m-%d') >= #{startDate}
and date_format(create_date, '%Y-%m-%d') <= #{endDate}
GROUP BY date_format(create_date, '%Y-%m-%d')

select date_format(create_date, '%Y-%m') as dataRow, count(id) as dataCount from ct_merchant
and date_format(create_date, '%Y') = #{year}
GROUP BY date_format(create_date, '%Y-%m')

select
date_format(eat_time, '%Y-%m-%d') as dataRow,
sum(case when `status` = 10 and p_order_id = '0' then 1 else 0 end) as porderCount,
sum(case when `status` = 10 and p_order_id != '0' then 1 else 0 end) as orderCount
from
ct_master_order


         and date_format(eat_time, '%Y-%m-%d') >= #{startDate} 


         and date_format(eat_time, '%Y-%m-%d') <= #{endDate} 


        and merchant_id = #{merchantId}


GROUP BY date_format(eat_time, '%Y-%m-%d')

select date_format(eat_time, '%Y-%m') as dataRow, sum(case when `status` = 10 and p_order_id = '0' then 1 else 0 end) as porderCount, sum(case when `status` = 10 and p_order_id != '0' then 1 else 0 end) as orderCount from ct_master_order
and date_format(eat_time, '%Y') = #{year}
and merchant_id = #{merchantId}
GROUP BY date_format(eat_time, '%Y-%m')

