mapper.select:
SELECT ct.id, ct.password, ct.money, ct.batch, ct.type, ct.status, date_format(ct.open_card_date, '%Y-%m-%d %H:%i:%S') as openCardDate, (select s.username from sys_user s where s.id = ct.open_card_user) as openCardUserName, date_format(ct.bind_card_date, '%Y-%m-%d %H:%i:%S') as bindCardDate, (select s.username from ct_client_user s where s.id = ct.bind_card_user) as bindCardUserName, date_format(ct.create_date, '%Y-%m-%d %H:%i:%S') as createDate, ct.code FROM ct_card_info ct
and date_format(ct.create_date, '%Y-%m-%d') >= #{startDate}
and date_format(ct.create_date, '%Y-%m-%d') <= #{endDate}
order by ct.create_date desc

mapper.update:
update ct_card_info set
status = 2,
open_card_date = NOW(),
open_card_user = #{userId}
where status = 1 and id in

    #{id}

 update ct_card_info set
code = #{s}
 where id = #{id}
